# Função para lematizar o conjunto de dados
def lema(string):
    doc1 = string.lower()
    doc = nlp(doc1)
    return [token.lemma_ for token in doc]

# Função para remoção de stop words
def removestopwords_test(text):
    frases = []
    for palavras in text:
        # Estou colocando todos as palavras que não tem as stopwords nessa variavel
        if palavras not in stops:
            frases.append(palavras)
    return frases

# Função para vetorizar o texto de entrada
def vetorizar_palavra(text):
    vetor = []
    for i in range(len(termos)):
        count = 0
        for j in range(len(text)):
            if termos[i] == text[j]:
                count = count + 1
        vetor.append(count)
    vetor = np.array([vetor])
    return vetor

# Carregando modelo
#model = pickle.load(open('model.pkl'))
model = joblib.load('model.pkl')
#Instanciando flask

app = Flask(__name__)

@app.route('/predict',methods=['POST'])
def predict():
    # recebendo texto para prever
    texto = request.get_json()
    # Criando data set
    df = pd.DataFrame(texto, index=[0])
    # Lematizar texto
    text_lematizado = lema(df['Comentario'][0])
    # Removendo Stops Words
    text_lematizado_sem_stop = removestopwords_test(text_lematizado)
    # Vetorizando texto
    text_lematizado_sem_stop_vetorizado = vetorizar_palavra(text_lematizado_sem_stop)
    # prevendo probabilidade do texto
    b = model.predict_proba(text_lematizado_sem_stop_vetorizado)

    if b[0][1]< 0.999:
        classe_prev = 'Feedback Negativo'
    else:
        classe_prev = 'Feedback Possitivo'
    
    df['Previsão'] = classe_prev

    return df.to_json(orient='records')
if __name__ == '__main__':
    app.run(host='127.0.0.1',port='5000')